simple_mmd:
classDiagram
  class User {
    +int id
    +string name
  }
  class Order {
    +int orderId
    +decimal total
  }
  User "1" --> "*" Order

advanced_mmd:
%%{init: {"theme":"base","themeVariables":{"fontFamily":"ui-sans-serif,system-ui"}}}%%
requirementDiagram
  title Requirements (advanced features)

  requirement R1 {
    id: R1
    text: Mermaid editor mode
    risk: low
    verifymethod: test
  }

  requirement R2 {
    id: R2
    text: Export to SVG/PNG/PDF
    risk: medium
    verifymethod: analysis
  }

  requirement R3 {
    id: R3
    text: Offline-first rendering
    risk: low
    verifymethod: inspection
  }

  element UI {
    type: component
    docref: "EditorPane"
  }

  element Exporter {
    type: component
    docref: "mmdc pipeline"
  }

  R1 - satisfies -> UI
  R2 - satisfies -> Exporter
  R3 - satisfies -> UI
  R2 - traces -> R1
